    // Choose a pivot element from the array
    int pivot = unsortedArray[unsortedArray.Length - 1];

    // Partition the array into two subarrays such that all elements less than or equal to the pivot are in the left subarray and all elements greater than the pivot are in the right subarray.
    int left = 0, right = unsortedArray.Length - 1;
    while (left < right)
    {
        while (left < right && unsortedArray[left] <= pivot) left++;
        while (left < right && unsortedArray[right] > pivot) right--;
        if (left < right)
        {
            int temp = unsortedArray[left];
            unsortedArray[left] = unsortedArray[right];
            unsortedArray[right] = temp;
            left++;
            right--;
        }
    }

    // Recursively sort the left and right subarrays until they are both sorted.
