        Sort(unsortedArray, left, unsortedArray.Length - left);
    }
    if (right > 0)
    {
        Sort(unsortedArray, right, unsortedArray.Length - right);
    }

    // Swap the pivot element with the smallest element in the right subarray.
    int tmp = unsortedArray[left];
    unsortedArray[left] = unsortedArray[right];
    unsortedArray[right] = tmp;

    // Repeat steps 2 to 4 until the array is sorted.
    while (left < unsortedArray.Length - 1 && unsortedArray[left] < unsortedArray[left + 1])
