#include <iostream>
using namespace std;

void partition(int nums[], int low, int high) {
    int pivot = nums[high];
    int i = low - 1;
    for (int j = low; j <= high - 1; j++) {
        if (nums[j] < pivot) {
            i++;
            swap(nums[i], nums[j]);
        }
    }
    swap(nums[i + 1], nums[high]);
}

void quicksort(int nums[], int low, int high) {
    if (low < high) {
        int partition_point = partition(nums, low, high);
        quicksort(nums, low, partition_point - 1);
        quicksort(nums, partition_point + 1, high);
    }
}

