public void reverseList(Node head) {
    if (head == null || head.next == null) {
        return;
    }
    Node prev = head;
    Node cur = head.next;
    while (cur != null) {
        prev.next = cur.next;
        cur.next = prev;
        prev = cur;
        cur = cur.next;
    }
    prev.next = null;
}
