function reverseString(string) {
    let reversedString = [];
    let reversedStringLen = 0;

    let reversedStringIndex = string.length - 1;
    let reversedStringChar = string[reversedStringIndex];
    reversedString.push(reversedStringChar);
    reversedStringLen += 1;

    while (reversedStringLen > 0) {
        reversedStringChar = reversedString[--reversedStringIndex];
        reversedString.push(reversedStringChar);
        reversedStringLen -= 1;
    }

    return reversedString.reverse().join("");
}

