    public QueueSolution(int capacity, int size) {
        if (capacity <= 0 || size <= 0) {
            throw new IllegalArgumentException("Invalid capacity or size specified.");
        }
        this.setCapacity(capacity);
        this.setSize(size);
    }

    // Returns the size of the Queue.
    public int getSize() {
        return this.size;
    }

    // Increments the size of the Queue by one.
    public void enqueue(int data) {
        if (this.size == this.getCapacity()) {
            this.expand();
        }
        this.firstStack.push(data);
        this.secondStack.push(data);
        this.size++;
    }

    // Returns the front element of the Queue.
    public int dequeue() {
        if (this.size == 0) {
            throw new IllegalStateException("Queue is empty.");
        }
        return this.firstStack.pop();
    }

    // Returns the rear element of the Queue.
    public int rear() {
        return this.secondStack.pop();
    }

    // Removes and returns the front element of the Queue.
    public int front() {
        if (this.size == 0) {
            throw new IllegalStateException("Queue is empty.");
        }
        return this.firstStack.pop();
    }

    // Removes and returns the rear element of the Queue.
