public class TwoStackQueue<E> implements Queue<E> {
    private Stack<E> front;
    private Stack<E> back;

    public TwoStackQueue() {
        front = new Stack<>();
        back = new Stack<>();
    }

    public boolean isEmpty() {
        return front.isEmpty() && back.isEmpty();
    }

    public boolean isFull() {
        return !isEmpty() && front.peek() == null;
    }

    public boolean offer(E e) {
        if (isEmpty()) {
            return false;
        }
        back.push(e);
        return true;
    }

    public E poll() {
        if (isEmpty()) {
            return null;
        }
        E e = front.pop();
        return e;
    }

    public E peek() {
        if (isEmpty()) {
            return null;
        }
        return front.peek();
    }

    public E element() {
        return poll();
    }

    public int size() {
        return front.size() + back.size();
    }

    public void clear() {
        front.clear();
        back.clear();
    }
}

