public class Stack {
    private StackElement[] stack;  // array to hold elements
    private int size;               // number of elements currently in the stack
    private int top;                // index of the element on top of the stack

    public Stack() {
        stack = new StackElement[DEFAULT_SIZE];
        size = 0;
        top = -1;
    }

    public boolean isEmpty() {
        return size == 0;
    }

    public int size() {
        return size;
    }

    public void push(StackElement element) {
        if (top == -1) {
            top = size;
        } else {
            top++;
        }
        stack[top] = element;
        size++;
    }

    public StackElement pop() {
        if (isEmpty()) {
            throw new EmptyStackException("Cannot pop an empty stack");
        }
        StackElement element = stack[top];
        top--;
        size--;
        return element;
    }

    public boolean peek() {
        if (isEmpty()) {
            throw new EmptyStackException("Cannot peek an empty stack");
        }
        return true;
    }
}

