#include <iostream>

using namespace std;

void quicksort(int arr[], int low, int high) {
    if (low < high) {
        int lo = low, hi = high;
        int pivot = arr[(lo + hi) / 2];
        while (lo < hi) {
            while (arr[lo] < pivot) 
                lo++;
            while (arr[hi] > pivot) 
                hi--;
            if (lo < hi) 
                swap(arr[lo], arr[hi]);
        }
        quicksort(arr, lo, hi - 1);
        quicksort(arr, hi + 1, high);
    }
}

