public static void SortIntArray(int[] arr, int start, int end) { 
    int len = end - start + 1; 
    int[] tmp = new int[len]; 
    int i = start; 
    int j = 0; 
    int pivot = arr[j]; 
    for(i = start; i < end; i++) { 
        if(arr[i] < pivot) { 
            tmp[j] = arr[i]; 
            j++; 
        } else { 
            tmp[i - start] = arr[i]; 
        } 
    } 
    for(i = 0; i < len; i++) { 
        arr[i + start] = tmp[i]; 
    } 
    SortIntArray(tmp, start, end - start + 1); 
}

