void SortArray(int[] array)
{
    // Create a temp variable to hold the pivot element
    int temp = array[0];

    // Partition the array into two subarrays such that all elements less than or equal to the pivot are in the left subarray and all elements greater than the pivot are in the right subarray
    for (int i = 0; i < array.Length; i++)
    {
        if (array[i] < temp)
        {
            // Move elements greater than the pivot element to the right subarray
            for (int j = i + 1; j < array.Length; j++)
            {
                array[j] = array[j + 1];
            }
            array[array.Length - 1] = temp;
            temp = array[i];
        }
    }

    // Recursively sort the left and right subarrays until the array is sorted
    for (int i = 0; i < array.Length; i++)
    {
        if (array[i] < temp)
        {
            SortArray(array, i, array.Length - 1);
        }
    }
}

