public String longestCommonSubsequence(String s, String t) {

  // Create a set of unique characters in s and t.
  Set<Character> set = new HashSet<>();
  for (int i = 0; i < s.length(); i++) {
    set.add(s.charAt(i));
  }
  for (int i = 0; i < t.length(); i++) {
    set.add(t.charAt(i));
  }

  // Create a string of the characters in s and t.
  String sT = s.toCharArray();
  String tT = t.toCharArray();

  // Iterate through the characters in s and t, comparing each character with every other character.
  for (int i = 0; i <= s.length(); i++) {
    for (int j = 0; j <= t.length(); j++) {
      if (set.contains(sT[i]) && set.contains(tT[j])) {
        // Found a match, add it to the result and increment the index of the current character.
        return sT[i] + tT[j];
      }
    }
  }
  // No matches found.
  return "";
}

