public int[] sortArray(int[] unsortedArray)
{
    int i = 0, j = unsortedArray.Length - 1;
    while (i < j)
    {
        // Find the element to partition the array
        int pivot = unsortedArray[i];
        for (; i < j; i++)
        {
            if (unsortedArray[i] < pivot)
            {
                break;
            }
        }
        // Partition the array
        for (; i < j; i++, j--)
        {
            unsortedArray[i] = unsortedArray[j];
        }
        // Reverse the elements in the right subarray
        for (; j < unsortedArray.Length - 1; j++)
        {
            unsortedArray[j + 1] = unsortedArray[j];
        }
        unsortedArray[unsortedArray.Length - 1] = unsortedArray[i];
        // Recursively sort the right subarray
        sortArray(unsortedArray, unsortedArray.Length - 1);
    }
    return unsortedArray;
}

