Here are the codes for the above question:

// Given two strings, find the longest common subsequence between them
public class LongestCommonSubsequence {

    public static void main(String[] args) {
        String a = "ABCDGH";
        String b = "AEDFHR";
        String[] aArray = a.split("");
        String[] bArray = b.split("");
        int[] aLengths = new int[aArray.length];
        int[] bLengths = new int[bArray.length];
        for (int i = 0; i < aLengths.length; i++) {
            aLengths[i] = aArray[i].length();
        }

        for (int i = 0; i < bLengths.length; i++) {
            bLengths[i] = bArray[i].length();
        }

        int[][] LCS = new int[bLengths.length][aLengths.length];
        for (int i = 0; i < bLengths.length; i++) {
            for (int j = 0; j < aLengths.length; j++) {
                if (i != j) {
                    if (aArray[j].equals(bArray[i])) {
                        LCS[i][j] = aLengths[j] + 1;
                    }
                }
            }
        }
        // print all LCSs from the longest to the shortest
        for (int i = 0; i < bLengths.length; i++) {
            for (int j = 0; j <= i; j++) {
                if (LCS[i][j] != 0) {
                    System.out.print(LCS[i][j] + " ");
                } else {
                    System.out.print("0 ");
                }
            }
            System.out.println();
        }
    }
}

