public static void Sort(int[] array)
{
    int n = array.Length;

    if (n == 0)
        return;

    // choose a pivot element from the array
    int pivotIndex = Partition(array, 0, n - 1);

    // partition the array such that all elements less than or equal to the pivot are in the left subarray and all elements greater than the pivot are in the right subarray
    Swap(array, 0, pivotIndex);
    int leftIndex = Partition(array, 0, pivotIndex - 1);
    Swap(array, pivotIndex, n - 1);
    int rightIndex = Partition(array, pivotIndex + 1, n - 1);

    // recursively sort the left and right subarrays until they are sorted
    Sort(array, 0, leftIndex - 1);
    Sort(array, pivotIndex + 1, rightIndex - 1);
}

private static int Partition(int[] array, int startIndex, int endIndex)
{
    int pivotIndex = getPivotIndex(array, startIndex, endIndex);
    Swap(array, startIndex, pivotIndex);

    int leftIndex = startIndex;
    int rightIndex = endIndex;
    while (leftIndex < rightIndex)
    {
        while (leftIndex < rightIndex && array[leftIndex] <= array[pivotIndex])
        {
            leftIndex++;
        }

        while (leftIndex < rightIndex && array[rightIndex] > array[pivotIndex])
        {
            rightIndex--;
        }

        if (leftIndex < rightIndex)
        {
            Swap(array, leftIndex, rightIndex);
        }
    }

    Swap(array, pivotIndex, rightIndex);
    return rightIndex;
}

private static int getPivotIndex(int[] array, int startIndex, int endIndex)
{
    int i = startIndex;
    int j = endIndex - 1;
    while (i <= j)
    {
        int k = (i + j) / 2;
        if (k < endIndex && array[k] > array[i])
