            i = k + 1;
        }
        else if (k < startIndex && array[k] < array[j])
        {
            j = k - 1;
        }
        else
        {
            return k;
        }
    }
    return i;
}

private static void Swap(int[] array, int i, int j)
{
    int tmp = array[i];
    array[i] = array[j];
    array[j] = tmp;
}
