class Stack {
    Node top;
    Node bottom;
    int size;
    Stack() {
        top = bottom = null;
        size = 0;
    }
    void push(int data) {
        if (top == null) {
            top = new Node(data, null);
            bottom = top;
            size++;
        } else {
            bottom.next = new Node(data, bottom);
            bottom = bottom.next;
            size++;
        }
    }
    int pop() {
        if (top == null) {
            return -1;
        } else {
            int data = top.data;
            top = top.next;
            size--;
            return data;
        }
    }
}

class Node {
    int data;
    Node next;
    Node(int data, Node next) {
        this.data = data;
        this.next = next;
    }
}
