int main()
{
    //Open the graph file
    ifstream graph_file("graph.txt");
    if (!graph_file)
    {
        cerr << "Error opening graph file!" << endl;
        return 1;
    }

    //Read the graph from the file
    graph_file >> vertices;
    graph_file >> edges;

    //Allocate space for the vector of shortest paths
    vector<int> shortest_paths;
    shortest_paths.reserve(vertices.size());

    //Get the length of the shortest path from the source vertex to itself
    int source_to_self_path_length = shortest_path(vertices, edges, vertices.begin(), vertices.begin());

    //For each vertex except the source vertex, update the shortest path from the source vertex to that vertex
    for (int i = 0; i < vertices.size(); i++)
    {
        //If the current vertex is not the source vertex, update the shortest path
        if (i != vertices.begin())
        {
            //Get the index of the current vertex
            int current_vertex_index = vertices.find(i);

            //Get the weight of the edge from the source vertex to the current vertex
            int weight = edges[current_vertex_index];

            //Update the shortest path from the source vertex to the current vertex
            shortest_paths[i] = shortest_path(vertices, edges, vertices.begin(), current_vertex_index) + weight;
        }
    }

    //Iterate through the vector of shortest paths to print the result
    for (int i = 0; i < shortest_paths.size(); i++)
